package primitive;

public class Primitive_demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		all();
	}	
	public static void all()
	{
		byte b=100;
		short s=500;
		int a=6000;
		long q=1234567891;
		float f=0.0012f;
		double n=0.71355445536d;
		boolean val=true;
		boolean flash=false;
		char ch1=88;
		char ch2='y';
		System.out.println("value of byte is "+ b);
		System.out.println("value of short is"+s);
		System.out.println("value of integer is "+a);
		System.out.println("value of long is "+q);
		System.out.println("value of float is "+f);
		System.out.println("value of double is"+n);
		System.out.println("boolean is "+val);
		System.out.println("boolean is "+flash);
		System.out.println("char is "+ch1);
		System.out.println("char is "+ch2);
		System.out.println("program is over");
		
	}

}
